import React from 'react';
import { connect } from 'react-redux';
import { signIn, signOut } from '../actions';

class GoogleAuth extends React.Component {
    state = { isSignedIn: null };

    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '1034803915396-cl1ha0u0vrj2tp954fpbqb58k6ntuu0f.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn: this.auth.isSignedIn.get() });
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = () => {
        this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    }

    onSingIn = () => {
        this.auth.signIn();
    }

    onSingOut = () => {
        this.auth.signOut();
    }

    renderAuthButton() {
        switch(this.state.isSignedIn) {
            case true:
                return (
                    <button onClick={this.onSingOut} className="ui red google button">
                        <i className="google icon" />
                        Sign Out!
                    </button>
                );
                break;
            case false:
                return (
                    <button onClick={this.onSingIn} className="ui red google button">
                        <i className="google icon" />
                        Sign In with Google!
                    </button>
                );
                break
            default:
                return null;
        }
    }

    render() {
        return <div>{this.renderAuthButton()  }</div>;
    }
}

export default GoogleAuth;
