import React from 'react';
import { connect } from 'react-redux';
import Modal from '../Modal';
import history from '../../history';
import { fetchStream, deleteStream } from '../../redux/actions';


class StreamDelete extends React.Component {
    componentDidMount() {
        this.props.fetchStream(this.props.match.params.id);
    }

    renderActions() {
        return (
            <React.Fragment>
                <div className="ui negative button">Delete</div>
                <div 
                    onClick={() => this.props.deleteStream(this.props.stream.id)} 
                    className="ui button"
                >
                        Cancel
                </div>
            </React.Fragment>
        )
    }

    render() {
        console.log(this.props.stream);
        if (this.props.stream) {
            return (
                <div>
                    StreamDelete
                    <Modal 
                        title="Delete Stream"
                        content={`Are you sure you want to delete ${this.props.stream.title}?`}
                        actions={this.renderActions()}
                        onDismiss={() => history.push('/')}
                    />
                </div>
            );
        } else {
            return null;
        }
    }
}

const mapStateToProps = (state, ownProps) => {
    return { stream: state.streams[ownProps.match.params.id] }
}

export default connect(mapStateToProps, { fetchStream, deleteStream })(StreamDelete);
