import { SIGN_IN, SIGN_OUT } from './types';
import streams from '../api/streams';

export const signIn = (userId) => {
    return {
        type: SIGN_IN,
        payload: userId
    };
};

export const signOut = () => {
    return {
        type: SIGN_OUT
    };
};

export const createStream = (formValues) => {
    return async (dispatch) => {
        streams.post('/streams', formValues);
    }
};
